(function(win,doc){
    'use strict';

    let defaultUrl;

    win.userdefaultValues = (function()
    {
        return {
            userdefaultValues: function (pUrl, pStatusDemand){
                defaultUrl      = pUrl;
            }
        }
    })();

    $(function () {
        $('form[name="loginUserDefault"]').submit(function (event) {

            event.preventDefault();

            $.ajax({
                url: defaultUrl+'/usuario/login-email',
                type: "post",
                headers: {
                    'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                },
                data: $(this).serialize(),
                dataType: "json",
                success: function (response) {
                    if(response.success === true){
                        alert(response.message)
                    }else{
                        alert('error');
                    }
                }
            });


        })
    });

})(window, document);
